function MainGUI:warning(text)
  echo("\n\n\n")
  decho("<255,255,0:0,0,0>WARNUNG (MainGUI): ")
  echo(text)
  echo("\n\n\n")
end

function MainGUI:error(text)
  echo("\n\n\n")
  decho("<255,155,0:0,0,0>FEHLER (MainGUI): ")
  echo(text)
  echo("\n\n\n")
end

function MainGUI:info(text)
  echo("\n\n\n")
  decho("<120,170,255:0,0,0>INFO (MainGUI): ")
  echo(text)
  echo("\n\n\n")
end
